import { keyframes } from "styled-components";
import styled    from "styled-components";
import { activeLogColor } from "../LogsWindow/LogWindow.style";


const SpinningAnimation = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`

export const LoaderRingAnimation = styled("div")`
  position: fixed;
  top: 20vh;
  left: 45vw;
  z-index: 10;
  border: 16px solid grey; /* Light grey */
  border-top: 16px solid ${activeLogColor}; /* Blue */
  border-radius: 50%;
  background-color: transparent;
  opacity: 0.5;
  width: 80px;
  height: 80px;
  animation: ${SpinningAnimation} 2s ease-in-out infinite;
`
